export default {
  name: 'ReportView',
  // 1. 添加多单词组件名
  data() {
    return {
      reportData: []
    };
  },
  created() {
    this.fetchReport();
  },
  methods: {
    async fetchReport() {
      // 2. 使用 async/await 优化异步操作
      try {
        const {
          data
        } = await this.$axios.get('/api/report');
        this.reportData = data;
      } catch (error) {
        console.error('获取报告失败:', error);
        this.$message.error('获取报告失败，请稍后重试'); // 3. 添加用户友好提示
      }
    }
  }
};
