import Vue from 'vue';
import VueRouter from 'vue-router';
import Login from '@/components/Login.vue'; // 引入登录组件
import Upload from '@/components/Upload.vue'; // 引入上传组件
import Report from '@/components/Report.vue'; // 引入报告组件

Vue.use(VueRouter);

// 定义路由
const routes = [
  {
    path: '/login',
    name: 'Login',
    component: Login,
  },
  {
    path: '/upload',
    name: 'Upload',
    component: Upload,
  },
  {
    path: '/report',
    name: 'Report',
    component: Report,
  },
  {
    path: '/',
    redirect: '/login', // 默认重定向到登录页
  },
];

// 创建路由实例
const router = new VueRouter({
  mode: 'history',
  routes,
});

export default router;
