import "core-js/modules/es.array.push.js";
export default {
  data() {
    return {
      loginForm: {
        username: '',
        password: ''
      },
      rules: {
        username: [{
          required: true,
          message: '请输入用户名',
          trigger: 'blur'
        }],
        password: [{
          required: true,
          message: '请输入密码',
          trigger: 'blur'
        }]
      }
    };
  },
  methods: {
    submitForm(formName) {
      this.$refs[formName].validate(valid => {
        if (valid) {
          // 调用登录接口
          this.$axios.post('/api/login', this.loginForm).then(response => {
            // 处理登录成功逻辑
            this.$router.push('/dashboard');
          }).catch(error => {
            // 处理登录失败逻辑
            console.error(error);
          });
        } else {
          console.log('表单验证失败');
          return false;
        }
      });
    }
  }
};
